var evilscan = require('./');

var scanOptions = {
	target:'192.168.56.106',
	port:'21-23,25,37,42,53,69,70,79,80,109,110,115,118,119,137,139,143,150,156,161,179,194,443,1900',
	status:'TROU', // Timeout, Refused, Open, Unreachable
	display: 'console',
	banner:true
};

var scanner = new evilscan(scanOptions);

scanner.on('result',function(data) {
	console.log('%s|%s|%s|%s',data.ip,data.port,data.banner,data.status);
});

scanner.on('error',function(err) {
	throw new Error(data.toString());
});

scanner.on('done',function() {
});

scanner.run();
 
